using System;\nusing System.Reflection;\nusing System.Web.Services;\nusing System.Web.Services.Protocols;\n\nnamespace Whatever\n{\n    [WebServiceBinding(Name = "", Namespace = "")]\n    public class WebServiceInvoker : SoapHttpClientProtocol\n    {\n        public WebServiceInvoker(string url, string ns, string bindingName)\n        {\n            Url = url;\n            // Optionally set credentials etc.\n        }\n\n        public object DynamicInvoke(string methodName, object[] parameters)\n        {\n            Type thisType = this.GetType();\n            MethodInfo methodInfo = thisType.GetMethod(methodName);\n\n            if (methodInfo == null)\n            {\n                throw new Exception($"Method {methodName} not found.");\n            }\n\n            return methodInfo.Invoke(this, parameters);\n        }\n\n        public object InvokeFunction(string methodName, params object[] args)\n        {\n            return DynamicInvoke(methodName, args);\n        }\n\n        public void InvokeAction(string methodName, params object[] args)\n        {\n            DynamicInvoke(methodName, args);\n        }\n    }\n}