using System;\nusing System.Data.Entity;\n\npublic class LogEntities : DbContext\n{\n    public DbSet<AuditLog> AuditLogs { get; set; }\n    // Add other DbSet properties as needed\n}\n\npublic class AuditLog\n{\n    public long EventId { get; set; }\n    // Add other properties as needed\n}\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        using (var db = new LogEntities())\n        {\n            var nextValQuery = "SELECT NEXT VALUE FOR AUDITLOG.EVENTID_SEQ FROM sysibm.sysdummy1";\n            var nextVal = db.Database.SqlQuery<long>(nextValQuery).First();\n            Console.WriteLine("Next sequence value: " + nextVal);\n        }\n    }\n}