using FlaUI.Core;\nusing FlaUI.Core.AutomationElements;\nusing FlaUI.UIA3;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        using (var automation = new UIA3Automation())\n        {\n            var app = FlaUI.Core.Application.Launch("PathToYourWpfApp.exe");\n            var mainWindow = app.GetMainWindow(automation);\n\n            // Find the element to right click on\n            var elementToRightClick = mainWindow.FindFirstDescendant(cf => cf.ByAutomationId("YourElementAutomationId"));\n\n            // Perform right-click\n            elementToRightClick.RightClick();\n\n            // Find and click the menu item\n            var menuItem = mainWindow.FindFirstDescendant(cf => cf.ByName("MenuItemName"));\n            menuItem?.Click();\n\n            app.Close();\n        }\n    }\n}