// Create a DataRelation between the tables\nDataColumn parentColumn = dsTabData.Tables["ParentTabs"].Columns["fsp_fsid"];\nDataColumn childColumn = dsTabData.Tables["FilingTabs"].Columns["fs_ID"];\nDataRelation relation = new DataRelation("FilingToParent", parentColumn, childColumn, false);\ndsTabData.Relations.Add(relation);\n\n// Get the rows from FilingTabs that do not have a corresponding row in ParentTabs\nDataTable filingTabs = dsTabData.Tables["FilingTabs"];\nDataRow[] filteredRows = filingTabs.Select().Where(row => row.GetParentRows(relation).Length == 0).ToArray();\n\n// If you need these rows as a DataTable, you can copy them to a new DataTable\nDataTable resultTable = filingTabs.Clone();\nforeach (DataRow row in filteredRows)\n{\n    resultTable.ImportRow(row);\n}\n\n// Now resultTable contains the rows from FilingTabs that do not have a corresponding row in ParentTabs