using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string curpath = Path.GetDirectoryName(Application.ExecutablePath);\n        string workingDirectory = Path.Combine(curpath, "snd");\n        string omgwut = "copy /b a.wav + b.wav + c.wav output.wav"; // Your actual command\n\n        Process proc0 = new Process();\n        proc0.StartInfo.FileName = "cmd.exe";\n        proc0.StartInfo.Arguments = $"/c {omgwut}";\n        proc0.StartInfo.WorkingDirectory = workingDirectory;\n        proc0.StartInfo.RedirectStandardOutput = true;\n        proc0.StartInfo.RedirectStandardError = true;\n        proc0.StartInfo.UseShellExecute = false;\n        proc0.StartInfo.CreateNoWindow = true;\n\n        proc0.Start();\n\n        string output = proc0.StandardOutput.ReadToEnd();\n        string error = proc0.StandardError.ReadToEnd();\n\n        proc0.WaitForExit();\n\n        Console.WriteLine("Output: " + output);\n        Console.WriteLine("Error: " + error);\n    }\n}