using System;\nusing System.Web;\n\nnamespace HandlerLib\n{\n    public class testhandler : IHttpHandler\n    {\n        public void ProcessRequest(HttpContext context)\n        {\n            string input = context.Request.Url.Segments[context.Request.Url.Segments.Length - 1];\n            context.Response.ContentType = "text/plain";\n            context.Response.Write("Your Input Was: " + input);\n        }\n\n        public bool IsReusable\n        {\n            get { return false; }\n        }\n    }\n}