using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class CustomListViewForm : Form\n{\n    private ListView listView;\n\n    public CustomListViewForm()\n    {\n        listView = new ListView\n        {\n            Dock = DockStyle.Fill,\n            View = View.Details,\n            OwnerDraw = true // Enable owner drawing\n        };\n\n        // Add columns to the ListView\n        listView.Columns.Add("Template Categories", -2, HorizontalAlignment.Left);\n        listView.Columns.Add("Microsoft Office Online", -2, HorizontalAlignment.Left);\n\n        // Add some items\n        listView.Items.Add(new ListViewItem("Business"));\n        listView.Items.Add(new ListViewItem("Education"));\n        listView.Items.Add(new ListViewItem("Home"));\n        listView.Items.Add(new ListViewItem("Finance"));\n\n        // Handle the drawing events\n        listView.DrawColumnHeader += ListView_DrawColumnHeader;\n        listView.DrawItem += ListView_DrawItem;\n\n        this.Controls.Add(listView);\n    }\n\n    private void ListView_DrawColumnHeader(object sender, DrawListViewColumnHeaderEventArgs e)\n    {\n        // Draw the column headers\n        using (Brush brush = new SolidBrush(Color.LightGray))\n        {\n            e.Graphics.FillRectangle(brush, e.Bounds);\n        }\n        e.Graphics.DrawRectangle(Pens.Black, e.Bounds);\n        e.Graphics.DrawString(e.Header.Text, e.Font, Brushes.Black, e.Bounds);\n    }\n\n    private void ListView_DrawItem(object sender, DrawListViewItemEventArgs e)\n    {\n        // Draw the items\n        e.DrawBackground();\n        e.Graphics.DrawString(e.Item.Text, e.Item.Font, Brushes.Black, e.Bounds);\n        e.DrawFocusRectangle();\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new CustomListViewForm());\n    }\n}