using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class MyObject\n{\n    public int Value { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create a List<MyObject>\n        List<MyObject> myList = new List<MyObject> \n        {\n            new MyObject { Value = 3 },\n            new MyObject { Value = 1 },\n            new MyObject { Value = 2 }\n        };\n\n        // Convert List<MyObject> to IEnumerable<MyObject>\n        IEnumerable<MyObject> myEnumerable = myList.AsEnumerable();\n\n        // Perform LINQ operations\n        myEnumerable = myEnumerable.OrderBy(x => x.Value);\n\n        // Convert back to List<MyObject>\n        List<MyObject> sortedList = myEnumerable.ToList();\n        \n        // Output sorted values\n        foreach (var obj in sortedList)\n        {\n            Console.WriteLine(obj.Value);\n        }\n    }\n}