protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        if (Request.Cookies["PopupShown"] == null)\n        {\n            hypPopup.Attributes.Add("onclick",\n                "window.open('Popup.aspx', '', 'height=650, width=800, location=no, toolbar=no, status=no, scrollbars=yes, resizable=yes'); document.cookie = 'PopupShown=true; path=/'; return false");\n        }\n    }\n}