using Microsoft.AnalysisServices.AdomdClient;\n\nstring connectionString = "Data Source=<server>;Catalog=<database>";\nusing (AdomdConnection connection = new AdomdConnection(connectionString))\n{\n    connection.Open();\n    string mdxQuery = "SELECT { [Measures].[Sales Amount] } ON COLUMNS, { [Date].[Calendar].[Calendar Year].Members } ON ROWS FROM [Sales]";\n    \n    using (AdomdCommand command = new AdomdCommand(mdxQuery, connection))\n    {\n        using (AdomdDataReader reader = command.ExecuteReader())\n        {\n            while (reader.Read())\n            {\n                Console.WriteLine($"{reader.GetString(0)}: {reader.GetDecimal(1)}");\n            }\n        }\n    }\n}