using System;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        if (Console.IsOutputRedirected)\n        {\n            Console.WriteLine("Output is redirected.");\n        }\n        else\n        {\n            Console.WriteLine("Output is not redirected.");\n            ShowProgress();\n        }\n\n        // Rest of your application code\n    }\n\n    static void ShowProgress()\n    {\n        string[] animation = { "/", "-", "\\", "|" };\n        for (int i = 0; i < 100; i++)\n        {\n            Console.Write($"\r{animation[i % animation.Length]} {i}%");\n            System.Threading.Thread.Sleep(100); // Simulate work\n        }\n        Console.WriteLine("\rDone!         ");\n    }\n}