using System;\nusing Microsoft.VisualStudio.DebuggerVisualizers;\nusing System.Windows.Forms;\n\n[assembly: System.Diagnostics.DebuggerVisualizer(\n    typeof(GuidVisualizer),\n    typeof(VisualizerObjectSource),\n    Target = typeof(Guid),\n    Description = "Guid Visualizer")]\n\npublic class GuidVisualizer : DialogDebuggerVisualizer\n{\n    protected override void Show(IDialogVisualizerService windowService, IVisualizerObjectProvider objectProvider)\n    {\n        Guid guid = (Guid)objectProvider.GetObject();\n        MessageBox.Show(guid.ToString(), "Guid Visualizer");\n    }\n\n    public static void TestShowVisualizer(object objectToVisualize)\n    {\n        VisualizerDevelopmentHost visualizerHost = new VisualizerDevelopmentHost(objectToVisualize, typeof(GuidVisualizer));\n        visualizerHost.ShowVisualizer();\n    }\n}