using System.Web.Mvc;\nusing System.Web.Mvc.Html;\n\npublic static class HtmlHelperExtensions\n{\n    public static MvcHtmlString CustomValidationSummary(this HtmlHelper htmlHelper, string message, object htmlAttributes = null)\n    {\n        if (htmlHelper.ViewData.ModelState.IsValid)\n        {\n            return MvcHtmlString.Empty;\n        }\n\n        var validationSummary = htmlHelper.ValidationSummary(message, htmlAttributes);\n        if (validationSummary == null)\n        {\n            return MvcHtmlString.Empty;\n        }\n\n        var divTag = new TagBuilder("div");\n        divTag.AddCssClass("validation-summary");\n        divTag.InnerHtml = validationSummary.ToHtmlString();\n\n        return MvcHtmlString.Create(divTag.ToString());\n    }\n}