string currentDir = Server.MapPath(".");\nstring rootDirTilde = Server.MapPath("~");\nstring rootDirBackslash = Server.MapPath(@"\");\nstring rootDirSlash = Server.MapPath("/");\n\nResponse.Write("Current Directory: " + currentDir + "<br>");\nResponse.Write("Root Directory (~): " + rootDirTilde + "<br>");\nResponse.Write("Root Directory (\\): " + rootDirBackslash + "<br>");\nResponse.Write("Root Directory (/): " + rootDirSlash + "<br>");