#define TEST // Define the symbol for demonstration purposes\n\nusing System;\nusing System.Diagnostics;\n\npublic class TestClass\n{\n    [Conditional("TEST")]\n    public void TestableMethod()\n    {\n        Console.WriteLine("This method is included only when the TEST symbol is defined.");\n    }\n\n    public void NormalMethod()\n    {\n        Console.WriteLine("This method is always included.");\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        TestClass testClass = new TestClass();\n        testClass.TestableMethod(); // Will execute only if TEST symbol is defined\n        testClass.NormalMethod();   // Will always execute\n    }\n}