using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string sourceConnectionString = "your_source_connection_string";\n        string destinationConnectionString = "your_destination_connection_string";\n        \n        DataTable sourceData = GetDataTable(sourceConnectionString, "SELECT * FROM YourTable");\n        DataTable destinationData = GetDataTable(destinationConnectionString, "SELECT * FROM YourTable");\n\n        // Compare and sync logic here\n        foreach (DataRow sourceRow in sourceData.Rows)\n        {\n            bool exists = false;\n            foreach (DataRow destRow in destinationData.Rows)\n            {\n                if (sourceRow["PrimaryKeyColumn"].ToString() == destRow["PrimaryKeyColumn"].ToString())\n                {\n                    exists = true;\n                    // Update logic if needed\n                    break;\n                }\n            }\n            if (!exists)\n            {\n                // Insert logic if needed\n                InsertRow(destinationConnectionString, sourceRow);\n            }\n        }\n    }\n\n    static DataTable GetDataTable(string connectionString, string query)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlDataAdapter adapter = new SqlDataAdapter(query, connection);\n            DataTable dataTable = new DataTable();\n            adapter.Fill(dataTable);\n            return dataTable;\n        }\n    }\n\n    static void InsertRow(string connectionString, DataRow row)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string insertQuery = "INSERT INTO YourTable (Column1, Column2, ...) VALUES (@Value1, @Value2, ...)";\n            using (SqlCommand command = new SqlCommand(insertQuery, connection))\n            {\n                command.Parameters.AddWithValue("@Value1", row["Column1"]);\n                command.Parameters.AddWithValue("@Value2", row["Column2"]);\n                // Add parameters for other columns\n\n                connection.Open();\n                command.ExecuteNonQuery();\n                connection.Close();\n            }\n        }\n    }\n}