using System;\nusing System.Data.SqlClient;\nusing System.Web.UI;\n\npublic partial class ImagePopup : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Fetch the image data from the SQL Server database\n            // Assuming you have a method GetImageData() that returns the image URLs\n            var imageData = GetImageData();\n            ThumbnailImage.ImageUrl = imageData.ThumbnailUrl;\n            ThumbnailImage.Attributes["data-fullsize"] = imageData.FullsizeUrl;\n        }\n    }\n\n    private ImageData GetImageData()\n    {\n        // Retrieve image data from SQL Server\n        // Replace with your actual database access code\n        return new ImageData\n        {\n            ThumbnailUrl = "path/to/thumbnail.jpg",\n            FullsizeUrl = "path/to/fullsize.jpg"\n        };\n    }\n\n    private class ImageData\n    {\n        public string ThumbnailUrl { get; set; }\n        public string FullsizeUrl { get; set; }\n    }\n}