<% foreach (XElement segnalazione in ((XElement)ViewData["collezioneSegnalazioni"]).Elements("dossier")) { %>\n    <tr>\n        <td><%= Html.Encode((segnalazione.Element("NUM_DOSSIER") != null) ? segnalazione.Element("NUM_DOSSIER").Value : "") %></td>\n        <td><%= Html.Encode((segnalazione.Element("ANAG_RAGSOC_CGN") != null) ? segnalazione.Element("ANAG_RAGSOC_CGN").Value : "") %></td>\n        <td><%= Html.Encode((segnalazione.Element("ID_RIFATT_SEGN0") != null) ? segnalazione.Element("ID_RIFATT_SEGN0").Value : "") %></td>\n        <td><%= Html.Encode((segnalazione.Element("FLG_STATUS") != null) ? segnalazione.Element("FLG_STATUS").Value : "") %></td>\n        <td><%= Html.Encode((segnalazione.Element("DT_ACCADIMENTO") != null) ? segnalazione.Element("DT_ACCADIMENTO").Value : "") %></td>\n        <td><%= Html.Encode((segnalazione.Element("COD_RAMO_LUNA") != null) ? segnalazione.Element("COD_RAMO_LUNA").Value : "") %></td>\n    </tr>\n<% } %>