string CsvToHtml(string csv)\n{\n    var rows = csv.Split('\n');\n    var html = "<table>";\n    foreach (var row in rows)\n    {\n        html += "<tr>";\n        var cells = row.Split(',');\n        foreach (var cell in cells)\n        {\n            html += "<td>" + System.Net.WebUtility.HtmlEncode(cell.Trim()) + "</td>";\n        }\n        html += "</tr>";\n    }\n    html += "</table>";\n    return html;\n}