using System;\nusing System.Web;\n\npublic class MyRestServiceHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string method = context.Request.HttpMethod;\n        context.Response.ContentType = "application/json";\n        \n        if (method == "GET")\n        {\n            // Example: Handle GET request\n            string response = "{ \"message\": \"Hello, World!\" }";\n            context.Response.Write(response);\n        }\n        else if (method == "POST")\n        {\n            // Example: Handle POST request\n            var inputStream = new System.IO.StreamReader(context.Request.InputStream);\n            string requestBody = inputStream.ReadToEnd();\n            // Process the request body as needed\n            string response = "{ \"message\": \"Data received\", \"data\": " + requestBody + " }";\n            context.Response.Write(response);\n        }\n        else\n        {\n            context.Response.StatusCode = 405; // Method Not Allowed\n            context.Response.StatusDescription = "Method Not Allowed";\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}