using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        int processId = 1234; // Replace with the actual process ID\n        try\n        {\n            Process process = Process.GetProcessById(processId);\n            Console.WriteLine($"Process Name: {process.ProcessName}");\n            Console.WriteLine($"Process ID: {process.Id}");\n            Console.WriteLine($"Start Time: {process.StartTime}");\n            Console.WriteLine($"Total Processor Time: {process.TotalProcessorTime}");\n        }\n        catch (ArgumentException)\n        {\n            Console.WriteLine("Process with specified ID does not exist.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}