using (var context = new DataContext())\n{\n    var excludedStates = new List<string> { "CA", "IN", "MD" };\n    var query = from author in context.Authors\n                where !excludedStates.Contains(author.state)\n                select new { author.au_lname, author.state };\n    \n    foreach (var result in query)\n    {\n        Console.WriteLine($"{result.au_lname}, {result.state}");\n    }\n}