[ServiceContract]\npublic interface IComService\n{\n    [OperationContract]\n    string CallComMethod(string parameter);\n}\n\npublic class ComService : IComService\n{\n    public string CallComMethod(string parameter)\n    {\n        // Interact with your COM control here\n        var comControl = new ComControl();\n        comControl.SetParameter(parameter);\n        return comControl.GetResponse();\n    }\n}