// Example of generating a JWT token\npublic string GenerateToken(string clientId, string secretKey)\n{\n    var securityKey = new SymmetricSecurityKey(Encoding.UTF8.GetBytes(secretKey));\n    var credentials = new SigningCredentials(securityKey, SecurityAlgorithms.HmacSha256);\n\n    var claims = new[]\n    {\n        new Claim(JwtRegisteredClaimNames.Sub, clientId),\n        new Claim(JwtRegisteredClaimNames.Jti, Guid.NewGuid().ToString())\n    };\n\n    var token = new JwtSecurityToken(\n        issuer: "yourIssuer",\n        audience: "yourAudience",\n        claims: claims,\n        expires: DateTime.Now.AddMinutes(30),\n        signingCredentials: credentials);\n\n    return new JwtSecurityTokenHandler().WriteToken(token);\n}\n\n// Example of validating a JWT token\npublic ClaimsPrincipal ValidateToken(string token, string secretKey)\n{\n    var tokenHandler = new JwtSecurityTokenHandler();\n    var key = Encoding.ASCII.GetBytes(secretKey);\n\n    tokenHandler.ValidateToken(token, new TokenValidationParameters\n    {\n        ValidateIssuerSigningKey = true,\n        IssuerSigningKey = new SymmetricSecurityKey(key),\n        ValidateIssuer = false,\n        ValidateAudience = false,\n        ClockSkew = TimeSpan.Zero\n    }, out SecurityToken validatedToken);\n\n    return (ClaimsPrincipal)validatedToken;\n}