using System;\nusing Microsoft.Win32;\n\nclass Program\n{\n    static void Main()\n    {\n        string iisVersion = GetIISVersionFromRegistry();\n        Console.WriteLine("IIS Version: " + iisVersion);\n    }\n\n    static string GetIISVersionFromRegistry()\n    {\n        string version = string.Empty;\n        using (RegistryKey key = Registry.LocalMachine.OpenSubKey(@"SOFTWARE\Microsoft\InetStp"))\n        {\n            if (key != null)\n            {\n                object majorVersion = key.GetValue("MajorVersion");\n                object minorVersion = key.GetValue("MinorVersion");\n                if (majorVersion != null && minorVersion != null)\n                {\n                    version = majorVersion.ToString() + "." + minorVersion.ToString();\n                }\n            }\n        }\n        return version;\n    }\n}