using System;\nusing WMPLib;\n\nclass Program\n{\n    static void Main()\n    {\n        WindowsMediaPlayer player = new WindowsMediaPlayer();\n        player.URL = "your-media-file.mp3";\n\n        // Add another media file to the playlist\n        IWMPPlaylist playlist = player.currentPlaylist;\n        IWMPMedia media = player.newMedia("another-media-file.mp3");\n        playlist.appendItem(media);\n\n        player.controls.play();\n    }\n}