using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        List<char> unallowed = new List<char>();\n\n        AddRange(unallowed, '\u0000', '\u0008');\n        AddRange(unallowed, '\u000B', '\u000C');\n        // Add other ranges as needed\n\n        // Print the list to verify the result\n        foreach (char c in unallowed)\n        {\n            Console.WriteLine($"\\u{((int)c):X4}");\n        }\n    }\n\n    static void AddRange(List<char> list, char start, char end)\n    {\n        for (char c = start; c <= end; c++)\n        {\n            list.Add(c);\n        }\n    }\n}