using Microsoft.Win32;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            string regPath = @"HKEY_CLASSES_ROOT\http\shell\open\command";\n            string browserPath = (string)Registry.GetValue(regPath, "", null);\n            \n            if (browserPath != null)\n            {\n                // The path might contain extra arguments, extract the executable path\n                int exeStart = browserPath.IndexOf('"') + 1;\n                int exeEnd = browserPath.IndexOf('"', exeStart);\n                if (exeStart > 0 && exeEnd > exeStart)\n                {\n                    browserPath = browserPath.Substring(exeStart, exeEnd - exeStart);\n                }\n\n                Console.WriteLine("Default browser path: " + browserPath);\n            }\n            else\n            {\n                Console.WriteLine("Default browser path not found.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error retrieving default browser path: " + ex.Message);\n        }\n    }\n}