using System;\n\npublic class ComChecker\n{\n    public static bool IsComComponentInstalled(Guid clsid)\n    {\n        Type comType = Type.GetTypeFromCLSID(clsid);\n        return comType != null;\n    }\n\n    public static void Main(string[] args)\n    {\n        Guid comClsid = new Guid("Your-Com-Component-CLSID"); // Replace with your COM component's CLSID\n        if (IsComComponentInstalled(comClsid))\n        {\n            Console.WriteLine("The COM component is installed.");\n        }\n        else\n        {\n            Console.WriteLine("The COM component is not installed.");\n        }\n    }\n}