using System;\nusing System.Diagnostics;\n\npublic class TimestampTraceListener : TraceListener\n{\n    public override void Write(string message)\n    {\n        base.Write($"{DateTime.Now:yyyy-MM-dd HH:mm:ss.fff} {message}");\n    }\n\n    public override void WriteLine(string message)\n    {\n        base.WriteLine($"{DateTime.Now:yyyy-MM-dd HH:mm:ss.fff} {message}");\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        // Remove default listeners (optional)\n        Trace.Listeners.Clear();\n\n        // Add the custom TimestampTraceListener\n        Trace.Listeners.Add(new TimestampTraceListener());\n\n        // Test the Trace.WriteLine with timestamp\n        Trace.WriteLine("Something wrong!");\n    }\n}