using System;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Provider=Microsoft.ACE.OLEDB.12.0;Data Source=YourDatabase.accdb;";\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            connection.Open();\n            string selectQuery = "SELECT ID, YourTextDateField FROM YourTableName";\n            using (OleDbCommand selectCommand = new OleDbCommand(selectQuery, connection))\n            using (OleDbDataReader reader = selectCommand.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    int id = reader.GetInt32(0);\n                    string textDate = reader.GetString(1);\n                    \n                    DateTime parsedDate;\n                    if (DateTime.TryParse(textDate, out parsedDate))\n                    {\n                        string updateQuery = "UPDATE YourTableName SET YourDateField = ? WHERE ID = ?";\n                        using (OleDbCommand updateCommand = new OleDbCommand(updateQuery, connection))\n                        {\n                            updateCommand.Parameters.AddWithValue("@YourDateField", parsedDate);\n                            updateCommand.Parameters.AddWithValue("@ID", id);\n                            updateCommand.ExecuteNonQuery();\n                        }\n                    }\n                }\n            }\n        }\n    }\n}