using System;\nusing System.IO;\n\nclass CustomDelimiterParser\n{\n    public static void ParseFile(string filePath, char delimiter, char quoteChar)\n    {\n        using (StreamReader reader = new StreamReader(filePath))\n        {\n            string line;\n            while ((line = reader.ReadLine()) != null)\n            {\n                string[] fields = SplitLine(line, delimiter, quoteChar);\n                foreach (var field in fields)\n                {\n                    Console.WriteLine(field);\n                }\n            }\n        }\n    }\n\n    private static string[] SplitLine(string line, char delimiter, char quoteChar)\n    {\n        var fields = new System.Collections.Generic.List<string>();\n        bool inQuotes = false;\n        int start = 0;\n\n        for (int i = 0; i < line.Length; i++)\n        {\n            if (line[i] == quoteChar)\n            {\n                inQuotes = !inQuotes;\n            }\n            else if (line[i] == delimiter && !inQuotes)\n            {\n                fields.Add(line.Substring(start, i - start).Replace(quoteChar.ToString(), ""));\n                start = i + 1;\n            }\n        }\n\n        // Add the last field\n        fields.Add(line.Substring(start).Replace(quoteChar.ToString(), ""));\n\n        return fields.ToArray();\n    }\n\n    public static void Main(string[] args)\n    {\n        string filePath = "path/to/your/file.txt";\n        char delimiter = 'þ';\n        char quoteChar = 'þ';\n\n        ParseFile(filePath, delimiter, quoteChar);\n    }\n}