using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\nclass Program\n{\n    static void Main()\n    {\n        Document doc = new Document();\n        try\n        {\n            PdfWriter.GetInstance(doc, new FileStream("output.pdf", FileMode.Create));\n            doc.Open();\n            doc.Add(new Paragraph("Hello, World!"));\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine(ex.Message);\n        }\n        finally\n        {\n            doc.Close();\n        }\n    }\n}