using System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic class AsyncSocketClient\n{\n    private Socket _clientSocket;\n    private const int BufferSize = 1024;\n    private byte[] _buffer = new byte[BufferSize];\n\n    public async Task StartClient(string serverIp, int serverPort)\n    {\n        _clientSocket = new Socket(AddressFamily.InterNetwork, SocketType.Stream, ProtocolType.Tcp);\n        await _clientSocket.ConnectAsync(new IPEndPoint(IPAddress.Parse(serverIp), serverPort));\n    }\n\n    public async Task SendPacketAsync(string data)\n    {\n        byte[] byteData = Encoding.ASCII.GetBytes(data);\n\n        try\n        {\n            await _clientSocket.SendAsync(new ArraySegment<byte>(byteData), SocketFlags.None);\n        }\n        catch (SocketException se)\n        {\n            Console.WriteLine($"SocketException: {se.Message}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Exception: {ex.Message}");\n        }\n    }\n\n    public void Close()\n    {\n        _clientSocket.Shutdown(SocketShutdown.Both);\n        _clientSocket.Close();\n    }\n}