// Extract the first 60 bytes and the last 20 bytes\nbyte[] data = new byte[60];\nbyte[] hashToValidate = new byte[20];\nArray.Copy(input, 0, data, 0, 60);\nArray.Copy(input, 60, hashToValidate, 0, 20);\n\n// Compute the HMACSHA1 hash of the first 60 bytes using the validation key\nHMACSHA1 hashSha = new HMACSHA1(_validationKey);\nbyte[] computedHash = hashSha.ComputeHash(data);\n\n// Compare the computed hash with the hash to validate\nbool isValid = computedHash.SequenceEqual(hashToValidate);\nreturn isValid;