using System;\nusing System.Net.Sockets;\nusing System.Text;\nusing System.Threading;\n\npublic class TelnetClient\n{\n    public static void Main()\n    {\n        string server = "your-mx-one-pbx-ip";\n        int port = 23;\n\n        try\n        {\n            using (TcpClient client = new TcpClient(server, port))\n            using (NetworkStream stream = client.GetStream())\n            {\n                byte[] buffer = new byte[1024];\n                int bytesRead;\n\n                // Adding delay to ensure server is ready\n                Thread.Sleep(1000);\n\n                // Read server response (login prompt)\n                bytesRead = stream.Read(buffer, 0, buffer.Length);\n                string response = Encoding.ASCII.GetString(buffer, 0, bytesRead);\n                Console.WriteLine("Received: " + response);\n\n                // Send login credentials\n                string login = "your-username\r\n";\n                string password = "your-password\r\n";\n\n                byte[] loginData = Encoding.ASCII.GetBytes(login);\n                byte[] passwordData = Encoding.ASCII.GetBytes(password);\n\n                stream.Write(loginData, 0, loginData.Length);\n                Thread.Sleep(500); // Wait for the server to process login\n                stream.Write(passwordData, 0, passwordData.Length);\n\n                // Read server response (post login)\n                bytesRead = stream.Read(buffer, 0, buffer.Length);\n                response = Encoding.ASCII.GetString(buffer, 0, bytesRead);\n                Console.WriteLine("Received: " + response);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Exception: " + ex.Message);\n        }\n    }\n}