using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define your connection string\n        string connectionString = "your_connection_string_here";\n\n        // Create a DataTable to hold the values\n        DataTable fooTable = new DataTable();\n        fooTable.Columns.Add("Value", typeof(string));\n\n        // Add the values to the DataTable\n        for (int i = 0; i < 50; i++)\n        {\n            fooTable.Rows.Add("value" + i);\n        }\n\n        // Use a SqlConnection to connect to the database\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            using (SqlCommand command = new SqlCommand("spFoo", connection))\n            {\n                command.CommandType = CommandType.StoredProcedure;\n\n                // Add the table-valued parameter\n                SqlParameter tvpParam = command.Parameters.AddWithValue("@FooTable", fooTable);\n                tvpParam.SqlDbType = SqlDbType.Structured;\n\n                // Execute the stored procedure\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}