using System.Net.Mail;\nusing System.Net.Mime;\n\npublic void SendEmailWithEmbeddedImage(string toEmail, string subject, string bodyHtml, string imagePath)\n{\n    // Create a new MailMessage object\n    MailMessage mailMessage = new MailMessage();\n    mailMessage.From = new MailAddress("your-email@example.com");\n    mailMessage.To.Add(toEmail);\n    mailMessage.Subject = subject;\n\n    // Create the HTML view with the image placeholder\n    string htmlBody = @"<html><body>" + bodyHtml + "<br><img src='cid:VisitorImage'></body></html>";\n    AlternateView htmlView = AlternateView.CreateAlternateViewFromString(htmlBody, null, MediaTypeNames.Text.Html);\n\n    // Create the LinkedResource for the image\n    LinkedResource visitorImage = new LinkedResource(imagePath, MediaTypeNames.Image.Jpeg);\n    visitorImage.ContentId = "VisitorImage";\n    visitorImage.TransferEncoding = TransferEncoding.Base64;\n\n    // Add the LinkedResource to the HTML view\n    htmlView.LinkedResources.Add(visitorImage);\n\n    // Add the HTML view to the MailMessage\n    mailMessage.AlternateViews.Add(htmlView);\n\n    // Configure the SMTP client and send the email\n    using (SmtpClient smtpClient = new SmtpClient("smtp.example.com"))\n    {\n        smtpClient.Credentials = new System.Net.NetworkCredential("your-username", "your-password");\n        smtpClient.EnableSsl = true;\n        smtpClient.Port = 587; // or the appropriate port for your SMTP server\n        smtpClient.Send(mailMessage);\n    }\n}\n\n// Usage example\nSendEmailWithEmbeddedImage("recipient@example.com", "Subject", "<p>Hello, here is an embedded image:</p>", "path/to/your/image.jpg");