public string GetCssRoot()\n{\n    string cssFile = "default";\n    HttpCookie cssCookie = Request.Cookies["css"];\n\n    if (cssCookie != null)\n    {\n        cssFile = cssCookie.Value;\n    }\n    else if (!string.IsNullOrEmpty(Request.QueryString["style"]))\n    {\n        cssFile = Request.QueryString["style"];\n        Response.Cookies["css"].Value = cssFile;\n        Response.Cookies["css"].Expires = DateTime.Now.AddDays(30); // Set cookie expiration as needed\n    }\n\n    return cssFile;\n}