// Assuming you have a reference to your ComponentArt grid\nComponentArt.Web.UI.Grid myGrid = // get your grid reference\n\n// Find the search box control within the grid\nTextBox searchTextBox = myGrid.FindControl("SearchTextBoxID") as TextBox;\nif (searchTextBox != null)\n{\n    // Clear the search text\n    searchTextBox.Text = string.Empty;\n}\n\n// Rebind the grid to refresh the data\nmyGrid.DataBind();