using System;\nusing Microsoft.Web.Administration;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string siteName = "Default Web Site";\n        string virtualDirectoryName = "/app";\n        string newPhysicalPath = @"C:\Projects\....\branches\Prod\"; // Change the path as needed\n\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site != null)\n            {\n                Application app = site.Applications[virtualDirectoryName];\n                if (app != null)\n                {\n                    app.VirtualDirectories["/"].PhysicalPath = newPhysicalPath;\n                    serverManager.CommitChanges();\n                    Console.WriteLine($"Virtual Directory '{virtualDirectoryName}' path updated to '{newPhysicalPath}'");\n                }\n                else\n                {\n                    Console.WriteLine($"Virtual Directory '{virtualDirectoryName}' not found.");\n                }\n            }\n            else\n            {\n                Console.WriteLine($"Site '{siteName}' not found.");\n            }\n        }\n    }\n}