using System;\nusing Microsoft.Web.Administration;\n\nclass Program\n{\n    static void Main()\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            string version = serverManager.ApplicationHost.Configuration.RootSection["system.applicationHost/version"].Attributes["IISVersion"].Value.ToString();\n            Console.WriteLine("IIS Version: " + version);\n        }\n    }\n}