public void UpdateDatabaseFromDataSet()\n{\n    // Store the selected row's identifier\n    int selectedRowId = -1;\n    if (dataGrid.SelectedItem != null)\n    {\n        DataRowView selectedRowView = (DataRowView)dataGrid.SelectedItem;\n        selectedRowId = (int)selectedRowView["ID"];\n    }\n\n    // Update the database\n    adapter.Update(exampleDataSet);\n    exampleDataSet.Clear();\n    adapter.Fill(exampleDataSet);\n\n    // Restore the selection\n    if (selectedRowId != -1)\n    {\n        foreach (DataRowView row in dataGrid.Items)\n        {\n            if ((int)row["ID"] == selectedRowId)\n            {\n                dataGrid.SelectedItem = row;\n                dataGrid.ScrollIntoView(row);\n                break;\n            }\n        }\n    }\n}