using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Define the external function with DllImport\n    [DllImport("YourNativeLibrary.dll", CallingConvention = CallingConvention.Cdecl)]\n    public static extern void Func(IntPtr bytes);\n\n    static void Main()\n    {\n        // Allocate and initialize the byte array\n        byte[] bytes = new byte[1000];\n\n        // Pin the array to prevent it from being moved by the garbage collector\n        GCHandle handle = GCHandle.Alloc(bytes, GCHandleType.Pinned);\n\n        try\n        {\n            // Get the pointer to the byte array\n            IntPtr ptr = handle.AddrOfPinnedObject();\n\n            // Call the external function\n            Func(ptr);\n        }\n        finally\n        {\n            // Free the handle\n            handle.Free();\n        }\n    }\n}