using System;\nusing System.Data;\nusing System.IO;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string xmlData = @"<?xml version=""1.0"" encoding=""utf-8""?>\n<CommentItems>\n  <Comment id=""1"">\n    <ID>1</ID>\n    <Author>Billy</Author>\n    <Date>05/22/2009</Date>\n    <Text>Comments about Bills Blog.</Text>\n  </Comment>\n  <Comment id=""3"">\n    <ID>3</ID>\n    <Author>Samanth</Author>\n    <Date>05/31/2009</Date>\n    <Text>Comments about Sams Blog.</Text>\n  </Comment>\n  <Comment id=""4"">\n    <ID>4</ID>\n    <Author>BillBoy</Author>\n    <Date>06/22/2009</Date>\n    <Text>Comments about Bills Blog.</Text>\n  </Comment>\n  <Comment id=""4"">\n    <ID>4</ID>\n    <Author>Bill</Author>\n    <Date>05/21/2009</Date>\n    <Text>Comments about Lees Blog.</Text>\n  </Comment>\n  <Comment id=""3"">\n    <ID>3</ID>\n    <Author>Bill</Author>\n    <Date>6/3/2009 2:29:54 PM</Date>\n    <Text>Bill's comment</Text>\n  </Comment>\n  <Comment id=""4"">\n    <ID>4</ID>\n    <Author>Bill</Author>\n    <Date>05/25/2009</Date>\n    <Text>Comments about Not Not Not Bills Blog.</Text>\n  </Comment>\n</CommentItems>";\n\n        DataSet dset = new DataSet();\n        DataTable CommentTable = new DataTable("Comment");\n        CommentTable.Columns.Add("ID", Type.GetType("System.Int32"));\n        CommentTable.Columns.Add("Author", Type.GetType("System.String"));\n        CommentTable.Columns.Add("Date", Type.GetType("System.String"));\n        CommentTable.Columns.Add("Text", Type.GetType("System.String"));\n\n        using (StringReader stringReader = new StringReader(xmlData))\n        {\n            CommentTable.ReadXml(stringReader);\n        }\n\n        dset.Tables.Add(CommentTable);\n\n        // Display the data to verify\n        foreach (DataRow row in CommentTable.Rows)\n        {\n            Console.WriteLine($"{row["ID"]}, {row["Author"]}, {row["Date"]}, {row["Text"]}");\n        }\n    }\n}