using System;\nusing System.Net;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string url = "http://www.test1.com/";\n\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n\n        try\n        {\n            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n            {\n                // Successfully got a response\n                Console.WriteLine("Status Code: " + response.StatusCode);\n                Console.WriteLine("Status Description: " + response.StatusDescription);\n            }\n        }\n        catch (WebException ex)\n        {\n            if (ex.Response is HttpWebResponse errorResponse)\n            {\n                // Error response received\n                Console.WriteLine("Status Code: " + errorResponse.StatusCode);\n                Console.WriteLine("Status Description: " + errorResponse.StatusDescription);\n\n                // Read the custom status description from the response headers\n                string customStatusDescription = errorResponse.StatusDescription;\n                Console.WriteLine("Custom Status Description: " + customStatusDescription);\n            }\n            else\n            {\n                // Handle other exceptions\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n        }\n    }\n}