protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initialize session variable\n        Session["MyData"] = "Initial Data";\n    }\n\n    // Check session variable during postbacks\n    if (Session["MyData"] == null)\n    {\n        // Handle the case where session variable is null\n        Response.Write("Session variable is null");\n    }\n    else\n    {\n        // Use the session variable\n        Response.Write("Session variable: " + Session["MyData"].ToString());\n    }\n}\n\nprotected void RadioButtonList_SelectedIndexChanged(object sender, EventArgs e)\n{\n    // Access session variable when radio button selection changes\n    var myData = Session["MyData"];\n    if (myData != null)\n    {\n        // Do something with myData\n    }\n    else\n    {\n        // Handle null session variable\n    }\n}