using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlContent = @"\n            <markup>\n                <element1 attributea='blah'>@@value1@@</element1>\n                <element2>@@value2@@</element2>\n                <element3>\n                    <element3point1>@@value1@@</element3point1>\n                    <element3point2>@@value3@@</element3point2>\n                    <element3point3>apple</element3point3>\n                </element3>\n                <element4>pear</element4>\n            </markup>";\n\n        XDocument xdoc = XDocument.Parse(xmlContent);\n\n        var tokens = xdoc.Descendants()\n                         .Where(e => e.Value.StartsWith("@@") && e.Value.EndsWith("@@"))\n                         .Select(e => e.Value)\n                         .Distinct()\n                         .ToList();\n\n        foreach (var token in tokens)\n        {\n            Console.WriteLine(token);\n        }\n    }\n}