protected void btnSearch_Click(object sender, EventArgs e)\n{\n    string keyword = txtKeyword.Text;\n    \n    // Basic input validation to prevent XPath injection\n    if (!string.IsNullOrWhiteSpace(keyword) && keyword.All(char.IsLetterOrDigit))\n    {\n        string xpathExpression = $"/rss/channel/item[contains(title, '{keyword}')]";\n        RSSFeedDataSource.XPath = xpathExpression;\n    }\n    else\n    {\n        // Handle invalid input appropriately\n        RSSFeedDataSource.XPath = "/rss/channel/item";\n    }\n}