using System;\nusing System.Runtime.InteropServices;\n\npublic class ComHelper : IDisposable\n{\n    private dynamic _comObject;\n    private bool _disposed = false;\n\n    public ComHelper()\n    {\n        // Initialize the COM object here (e.g., opening Word or Excel)\n        _comObject = new SomeComObject();\n    }\n\n    public void SomeComOperation()\n    {\n        if (_disposed)\n            throw new ObjectDisposedException("ComHelper");\n\n        // Perform operations with the COM object here\n        _comObject.DoWork();\n    }\n\n    protected virtual void Dispose(bool disposing)\n    {\n        if (!_disposed)\n        {\n            if (disposing)\n            {\n                // Dispose managed resources if any\n            }\n\n            // Release the COM object\n            if (_comObject != null)\n            {\n                Marshal.ReleaseComObject(_comObject);\n                _comObject = null;\n            }\n\n            _disposed = true;\n        }\n    }\n\n    public void Dispose()\n    {\n        Dispose(true);\n        GC.SuppressFinalize(this);\n    }\n\n    ~ComHelper()\n    {\n        Dispose(false);\n    }\n}